    private void jbBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbBuscarActionPerformed
          
        int cont = 0;
        
        try{
            int inputCodigo = Integer.parseInt(jtCodigo.getText());
            for(Producto produ:Menu.listaProducto){

                if(inputCodigo == produ.getCodigo() ){
                    cont++;
                    jtDescripcion.setText(produ.getDescripcion());
                    jtPrecio.setText(produ.getPrecio()+"");
                    jtStock.setText(produ.getStock()+"");
                    jcRubro.setSelectedItem(produ.getRubro());
                }   
            }
                if(cont==0){

                    JOptionPane.showMessageDialog(this,"No hay productos con este codigo");
        
            }
        }catch(NumberFormatException e){
           
            JOptionPane.showMessageDialog(this,"Ingrese solo numeros");
            jtCodigo.setText("");
        }    
    }//GEN-LAST:event_jbBuscarActionPerformed

    private void jbGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbGuardarActionPerformed
            
        String desc = jtDescripcion.getText();
        int cod = Integer.parseInt(jtCodigo.getText());
        int stk = Integer.parseInt(jtStock.getText());
        Double pre = Double.parseDouble(jtPrecio.getText());
        Categoria cat = (Categoria)jcRubro.getSelectedItem();

        Producto produ = new Producto(desc,cod,stk,pre,cat);

        Menu.listaProducto.add(produ);

        JOptionPane.showMessageDialog(this,"Producto Guardado");

        limpiarCeldas();
            
    }//GEN-LAST:event_jbGuardarActionPerformed

    private void jbEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbEliminarActionPerformed
        for(Producto produ:Menu.listaProducto){
                if(produ.getCodigo()==Integer.parseInt(jtCodigo.getText())){
                    Menu.listaProducto.remove(produ);
                    break;
                }
            }
           
            limpiarCeldas();

            JOptionPane.showMessageDialog(this,"Producto eliminado");
    }//GEN-LAST:event_jbEliminarActionPerformed

