    private void cargarBox(){
        jcRubro.setModel(new DefaultComboBoxModel<>(Categoria.values()));
        jcRubro.setSelectedIndex(-1);

    }
    
    public void limpiarCeldas(){
        
        jtCodigo.setText("");
        jtDescripcion.setText("");
        jtPrecio.setText("");
        jcRubro.setSelectedIndex(-1);
        jtStock.setText("");
    
    }
    
