    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        Double Pmax = Double.parseDouble(jtPrecioMax.getText());
        Double Pmin = Double.parseDouble(jtPrecioMin.getText());
        
        try{
            modelo.setRowCount(0);
            for(Producto produ:Menu.listaProducto){

                if(produ.getPrecio() <= Pmax && produ.getPrecio() >= Pmin){

                    modelo.addRow(new Object[]{
                        produ.getDescripcion(),
                        produ.getCodigo(),
                        produ.getStock(),
                        produ.getPrecio(),
                        produ.getRubro()});
                }    
            }    
        }catch(NumberFormatException e){
        
            JOptionPane.showMessageDialog(this, "Ingrese un valor en pesos");
        }    
           
    }//GEN-LAST:event_jButton1ActionPerformed


