    private void crearCabecera(){
        modelo.addColumn("DESCRIPCION");
        modelo.addColumn("CODIGO");
        modelo.addColumn("STOCK");
        modelo.addColumn("PRECIO");
        modelo.addColumn("RUBRO");
        jtTablaPrecios.setModel(modelo);
    }

